import React, { useEffect, useRef, useState } from "react";
import { CreateWebGPUCanvas } from "../Engine/Core";

function CanvasWidget() {
	const canvasElement = useRef(null);
	const [isPlaying, setIsPlaying] = useState(true);
	const [fps, setFps] = useState(0);
	const requestRef = useRef(null);
	const prevTimeRef = useRef(0);
	const frameCount = useRef(0);

	useEffect(() => {
		let canvasInstance;
		CreateWebGPUCanvas(800, 800).then((canvas) => {
			canvasInstance = canvas;
			if (canvasElement.current) {
				canvasElement.current.appendChild(canvas);
			}
		});

		const updateFps = (time) => {
			if (prevTimeRef.current === 0) {
				prevTimeRef.current = time;
			}

			frameCount.current++;
			const deltaTime = time - prevTimeRef.current;

			if (deltaTime >= 1000) {
				setFps(Math.round((frameCount.current * 1000) / deltaTime));
				prevTimeRef.current = time;
				frameCount.current = 0;
			}

			if (isPlaying) {
				requestRef.current = requestAnimationFrame(updateFps);
			}
		};

		if (isPlaying) {
			requestRef.current = requestAnimationFrame(updateFps);
		}

		return () => {
			if (canvasInstance && canvasElement.current) {
				canvasElement.current.removeChild(canvasInstance);
			}
			cancelAnimationFrame(requestRef.current);
		};
	}, [isPlaying]);

	const togglePlayPause = () => {
		setIsPlaying(!isPlaying);
	};

	return (
		<div className="player-window" style={styles.playerWindow}>
			<div ref={canvasElement} style={styles.canvasContainer}></div>
			<div className="controls" style={styles.controls}>
				<button onClick={togglePlayPause}>
					{isPlaying ? "Pause" : "Play"}
				</button>
				<span style={styles.fpsCounter}>FPS: {fps}</span>
			</div>
		</div>
	);
}

const styles = {
	playerWindow: {
		border: "2px solid #333",
		borderRadius: "8px",
		overflow: "hidden",
		width: "820px",
		height: "880px",
		display: "flex",
		flexDirection: "column",
		justifyContent: "space-between",
	},
	canvasContainer: {
		flex: 1,
	},
	controls: {
		padding: "10px",
		backgroundColor: "#222",
		color: "#fff",
		textAlign: "center",
		display: "flex",
		justifyContent: "space-between",
		alignItems: "center",
	},
	fpsCounter: {
		marginLeft: "10px",
		fontSize: "14px",
	},
};

export default CanvasWidget;
