import React, { useState } from "react";
import CanvasWidget from "./Canvas";

function Home() {
	const [showCanvas, setShowCanvas] = useState(false);

	const toggleCanvas = () => {
		setShowCanvas(!showCanvas);
	};
	return (
		<div style={styles.homePage}>
			<h1 style={styles.title}>Shaders-Web++</h1>
			<button onClick={toggleCanvas} style={styles.miniview}>
				Smiley Face
			</button>
			<div className="mini-view">{showCanvas && <CanvasWidget />}</div>
		</div>
	);
}

export default Home;

const styles = {
	homePage: {
		background: "rgb(166, 158, 158)",
		height: "160vh",
		width: "95vw",
		display: "block",
		margin: "auto",
		borderRadius: "5px",
	},
	title: {
		color: "black",
		textAlign: "center",
		fontSize: "75px",
		margin: "0",
		paddingTop: "5%",
		fontFamily: "Helvetica",
	},
	miniview: {
		background: "#FFFFFF",
		width: "20%",
		height: "10%",
		border: "none",
		transform: "translateY(600px)",
	},
};
